  flex-direction: column;
  align-items: flex-end;
  gap: 20px;
}

.chat-bubble {
  background-color: var(--card-background);
  border: 1px solid var(--card-border);
  border-radius: 0.4rem;
  padding: 20px;
  width: 100%;
  box-sizing: border-box;

  h3 {
    margin: 0;
